class CoreHello extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"}).appendChild(document.createElement("slot"))}static get observedAttributes(){return["lang","rainbow"]}connectedCallback(){this.hasAttribute("rainbow")?this.toggleRainbow("true"):this.toggleRainbow("false")}attributeChangedCallback(a,b,c){let d=this.getAttribute(a);d=c,"lang"==a?this.updateText(d,this.innerText):"rainbow"==a&&(this.hasAttribute("rainbow")?this.toggleRainbow("true"):this.toggleRainbow("false"))}disconnectedCallback(){}updateText(a,b){let c=this.innerText.split(" "),d=c[c.length-1];if("fr"==a){this.innerText="Salut Monde "+d}else if("sp"==a){this.innerText="Hola Mundo "+d}else if("en"==a){this.innerText="Hello World "+d}}toggleRainbow(a){"true"==a?this.classList.add("color-text-flow"):"false"==a&&this.classList.remove("color-text-flow")}}customElements.define("core-hello",CoreHello);